import React, { useState } from "react";
import { ArrowCircleUpIcon } from "@heroicons/react/solid";

const FooterChat = () => {
    const [message, setMessage] = useState(false);

    // console.log(message);
    const sendMessage = () => {
        console.log(message);
    };

    return (
        <footer className="inline-block relative">
            <form onSubmit={sendMessage}>
                {/* <MailIcon className="pointer-events-none w-8 h-8 absolute top-1/2 transform -translate-y-1/2 left-3" /> */}

                <input
                    value={message}
                    onChange={(e) => setMessage(e.target.value)}
                    placeholder="say something nice"
                    className="rounded-full bg-gray-500"
                />

                <button type="submit" disabled={!message} className={""}>
                    <ArrowCircleUpIcon className="pointer-events-none w-8 h-8 absolute top-1/2 transform -translate-y-1/2 left-3" />
                </button>
            </form>
        </footer>
    );
};

export default FooterChat;
