import React from "react";
import {
    ArrowCircleUpIcon,
    DocumentTextIcon,
    MailIcon,
} from "@heroicons/react/solid";
import { Github } from "react-bootstrap-icons";

const FooterChat = () => {
    // const [message, setMessage] = useState(false);

    return (
        <footer
            className="w-screen flex flex-row fixed inset-x-0 bottom-0"
            style={{ backgroundColor: "#FEFFFE" }}
        >
            <div className="w-5/12 h-4 md:h-10 relative md:w-1/6 pt-2.5 md:pt-2">
                <div
                    className="flex flex-row justify-around items-end text-white space-x-1 px-1"
                    // style={{ color: "#999999" }}
                >
                    <a
                        href="https://github.com/lucasz-hu"
                        target="_blank"
                        rel="noreferrer"
                    >
                        <div
                            className="px-2 md:px-3 py-1 rounded-full "
                            style={{ background: "#999999" }}
                        >
                            <Github className="w-6 h-6 md:w-8 md:h-8 " />
                        </div>
                    </a>
                    <a
                        href="https://lucasz-hu.github.io/lucaszhu_resume.pdf"
                        target="_blank"
                        rel="noreferrer"
                    >
                        <div
                            className="px-1  md:px-2 bg-black rounded-full "
                            style={{ background: "#999999" }}
                        >
                            <DocumentTextIcon className="w-8 h-8 md:w-10 md:h-10 " />
                        </div>
                    </a>
                    <a href="mailto:alucaszhu@gmail.com">
                        <div
                            className="px-1 md:px-2 bg-black rounded-full "
                            style={{ background: "#999999" }}
                        >
                            <MailIcon className="w-8 h-8 md:w-10 md:h-10" />
                        </div>
                    </a>
                </div>
            </div>
            <div
                className="py-2 relative mx-auto w-full md:pr-10 "
                style={{ color: "#C4C4C6" }}
            >
                <input
                    className="border-2 border-gray-300 bg-white h-10 px-5 pr-16 rounded-full text-sm focus:outline-none w-full "
                    type="text"
                    name="Send a Message"
                    placeholder="Send a Message"
                />
                <button
                    type="submit"
                    className="absolute right-0 top-0 mt-2.5 ml-3 md:pr-10"
                >
                    <ArrowCircleUpIcon
                        className="mr-1 w-9 h-9"
                        aria-hidden="true"
                        style={{
                            color: "#007AFF",
                        }}
                    />
                </button>
            </div>
        </footer>
    );
};

export default FooterChat;
